    PATCH /organisations/1/groups/1
    {
      'groupName': "Group Name",
      'roleInfo': null,
      'userInfo': null
    }

    PATCH /organisations/1/groups/1
    {
      'groupName': "",
      'roleInfo':{
          'addedRoleIds': [ 1, 2 ],
          'removedRoleIds': [ 3 ]
       },
      'userInfo':{
          'addedUserIds': [ "user1@mail.com", "user2@mail.com" ],
          'addedUserIds': [ "user3@mail.com" ]
       }
    }

    PATCH /organisations/1/groups/1
    {
      'groupName': null,
      'roleInfo':{
          'addedRoleIds': [ 1, 2 ],
